using System;
using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Soru_05
{
    internal class Program
    {
        static void Main(string[] args)
        {


            string num = "";

            int basamak = 13;
            Console.WriteLine(MaxProductThirTeen(num, basamak));

            Console.ReadKey();
        }

        private static long MaxProductThirTeen(string num, int basamak)
        {
            long maxProduct = 0;


            for (int i = 0; i < num.Length - basamak; i++)
            {
                long product = 1;
                for (int j = 0; j < basamak; j++)
                {
                    product *= long.Parse(num[i + j].ToString());
                }

                if (product > maxProduct) maxProduct = product;

                if (product == 23514624000)
                {
                    for(int k=0; k<basamak; k++)
                    {
                        Console.Write(num[i + k] + " ");
                    }
                }
            }
            Console.WriteLine();
            return maxProduct;
        }

    }
}
